function Nt_alert(options={}){this.typeValid=["error","success","warning","info"];this.opt=Object.assign({containernotification:null,destroy:false},options);this.build=function(){this._icoins={error:`<i class="fa-solid fa-triangle-exclamation"></i>`,warning:`<i class="fa-solid fa-circle-exclamation"></i>`,success:`<i class="fa-solid fa-circle-check"></i>`,info:`<i class="fa-solid fa-circle-info"></i>`,close:`<i class="fa-solid fa-xmark"></i>`};this._containerNotification=document.createElement("div");this._containerNotification.classList.add("nt-alert");this._item={};this._close={};this._allTag=null;return this};this.getItemAndClose=function(){return[this._item,this._close]};this.createNode=function(node={type:"",message:""}){node.type=node.type.trim().toLowerCase();if(!node.type){console.error("node empty !")}else{if(this._containerNotification.childElementCount){this._item["wrap-1"].remove();console.log("co gia tri: ")}if(this.typeValid.includes(node.type.trim().toLowerCase())){let stt=this._containerNotification.childElementCount;const nodeCurrent=document.querySelector(`wap-${stt}`);if(!this._containerNotification.contains(nodeCurrent)){const wrap=document.createElement("div");wrap.classList.add(`wrap-${stt+1}`,node.type,"box-notification");this._item[`wrap-${stt+1}`]=wrap;const wrapLeft=document.createElement("div");const wrapRight=document.createElement("div");const content=document.createElement("p");const button=document.createElement("button");const closeContent=document.createElement("button");closeContent.classList.add(`close-${stt+1}`,"button-close");closeContent.onclick=e=>{wrap.classList.add("none-notification");wrap.remove()};this._close[`close-${stt+1}`]=closeContent;button.classList.add("icon-circle",`icon-circle-${node.type}`);button.innerHTML=this._icoins[node.type];wrapLeft.classList.add("wrap-left");closeContent.innerHTML=this._icoins[`close`];wrapLeft.append(button);content.classList.add("wrap-content");content.innerText=node.message;wrapRight.classList.add("wrap-right");wrapRight.append(content);wrap.append(wrapLeft,wrapRight,closeContent);this._containerNotification.append(wrap);setTimeout((()=>{wrap.classList.add("hiden-notification")}),0);wrap.addEventListener("transitionend",(e=>{if(e.propertyName==="opacity"){console.log("opacity",e);wrap.remove()}}));document.body.append(this._containerNotification);console.log(this._containerNotification)}}else{console.warn("type not valid !")}}}}